# Day 2 - Part 2
# github.com/kek91

directions = [
'',
'',
'',
'',
''
]

numpad = [
    [' ', ' ', '1', ' ', ' '],
    [' ', '2', '3', '4', ' '],
    ['5', '6', '7', '8', '9'],
    [' ', 'A', 'B', 'C', ' '],
    [' ', ' ', 'D', ' ', ' ']
]

posindex = [2,0] # The starting position
posvalue = numpad[posindex[0]][posindex[1]] # 5

answer = [
    '',
    '',
    '',
    '',
    ''
]

for l in range(0, len(directions)):

    for i in range(0, len(directions[l])):

        currentdirection = directions[l][i]
        posvalue = numpad[posindex[0]][posindex[1]]

        if currentdirection == "U":
            if posvalue == "1" or posvalue == "2" or posvalue == "4" or posvalue == "5" or posvalue == "9":
                pass
            else:
                posindex[0] = posindex[0]-1
                posvalue = numpad[posindex[0]][posindex[1]]
            answer[l] += "" + posvalue

        elif currentdirection == "R":
            if posvalue == "1" or posvalue == "4" or posvalue == "9" or posvalue == "C" or posvalue == "D":
                pass
            else:
                posindex[1] = posindex[1]+1
                posvalue = numpad[posindex[0]][posindex[1]]
            answer[l] += "" + posvalue

        elif currentdirection == "D":
            if posvalue == "5" or posvalue == "A" or posvalue == "D" or posvalue == "C" or posvalue == "9":
                pass
            else:
                posindex[0] = posindex[0]+1
                posvalue = numpad[posindex[0]][posindex[1]]
            answer[l] += "" + posvalue

        elif currentdirection == "L":
            if posvalue == "1" or posvalue == "2" or posvalue == "5" or posvalue == "A" or posvalue == "D":
                pass
            else:
                posindex[1] = posindex[1]-1
                posvalue = numpad[posindex[0]][posindex[1]]
            answer[l] += "" + posvalue

print("\n\n")
for l in range(0, len(answer)):
    code = answer[l][len(answer[l])-1]
    print("Answer: " + str(answer[l]) + " ("+code+")\n")
